import React from "react";

import Chart from "../Chart/ChartBar";

const ExpensesChart = (props) => {
    const dataPoints = [
        { label: "Jan", value: 0 },
        { label: "Feb", value: 0 },
        { label: "Mar", value: 0 },
        { label: "Apr", value: 0 },
        { label: "May", value: 0 },
        { label: "Jun", value: 0 },
        { label: "Jul", value: 0 },
        { label: "Aug", value: 0 },
        { label: "Sep", value: 0 },
        { label: "Oct", value: 0 },
        { label: "Nov", value: 0 },
        { label: "Dec", value: 0 },
    ];

    // let barFillHeight = Math.round(()*100 + '%')
    const valueArray = props.expenses.map((element) => element.value);
    const totalMaxValue = Math.max(...valueArray);

    for (const expense of props.expenses) {
        const expenseMonth = expense.date.getMonth();
        dataPoints[expenseMonth] += expense.value;
    }

    return (
        <div>
            <Chart key={props.label} maxValue={totalMaxValue} />
        </div>
    );
};

export default ExpensesChart;
