import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");
    const [enteredDate, setEnteredDate] = useState("");
    // const [userInput, setUserInput] = useState({
    //     setEnteredTitle: "",
    //     setEnteredAmount: "",
    //     setEnteredDate: "",
    // });

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     enteredTitle: event.target.value,
        // });
        // setUserInput((prevState) => {
        //     return { ...prevState, enteredTitle: event.target.value };
        // });
    };

    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     enteredAmount: event.target.value, //we just overwrite the new values, ensures others are not thrown away
        // });
    };

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     enteredDate: event.target.value, //we just overwrite the new values, ensures others are not thrown away
        // });
    };

    const [stateValue, setStateValue] = useState("0");

    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate),
        };

        // console.log(expenseData);
        props.onSaveExpenseData(expenseData);
        setEnteredTitle("");
        setEnteredAmount("");
        setEnteredDate("");

        event.preventDefault();
        setStateValue((prevState) => {
            return (prevState = 1);
        });
    };

    const onSubmitHandler = (event) => {
        event.preventDefault();
        setStateValue((prevState) => {
            return (prevState = 1);
        });
    };

    const onAddHandler = (event) => {
        event.preventDefault();
        setStateValue((prevState) => {
            return (prevState = 0);
        });
    };

    if (stateValue !== 0) {
        return (
            <div className="new-expense__actions">
                <button onClick={onAddHandler}>Add New Expense</button>
            </div>
        );
    }

    return (
        <form className="" onSubmit={submitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input
                        type="text"
                        value={enteredTitle} //two way binding
                        onChange={titleChangeHandler}
                    ></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input
                        type="number"
                        min="0.01"
                        step="0.01"
                        value={enteredAmount}
                        onChange={amountChangeHandler}
                    ></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input
                        type="date"
                        min="2019-01-01"
                        max="2022-12-31"
                        value={enteredDate}
                        onChange={dateChangeHandler}
                    ></input>
                </div>
            </div>
            <div className="new-expense__actions">
                <button onClick={onSubmitHandler} type="submit">
                    Cancel
                </button>
                <button type="submit">Add Expense</button>
            </div>
        </form>
    );
};

export default ExpenseForm;
